/**
 * Classe contendo as configurações padrões para a aplicação AMB 2.0.
 *
 * @description
 * Esta classe disponibiliza propriedades estáticas para configurações gerais da aplicação. Cada aplicação deve sobrescrever
 * essas configurações conforme necessidade.
 *
 * @example
 * import 'AMBApplicationConfig' from '@amb/core';
 * AMBApplicationConfig.baseUrl = "";
 */
export declare class AMBApplicationConfig {
    /**
     * URL base da aplicação, por exemplo 'http://www.bradesco.com.br/'
     */
    static baseUrl: string;
    /**
     * Contexto da aplicação, por exemplo 'piloto-amb'
     */
    static webAppContextPath: string;
    /**
     * Url do serviço web
     */
    static webServiceUrl: string;
}
