interface AWBE {
	Drivers: Drivers;
	console: Console;
}

interface Drivers {
	load(driver: string): PinpadVx600;
	register(driver: PinpadVx600): void;
}

interface Console {
	log(log: string): void;
}

interface PinpadVx600 {
	estaConectado(
		onSuccess: (sucess: string) => void,
		onError: (error: string) => void): void;

	inicializar(
		onSuccess: (sucess: string) => void,
		onError: (error: string) => void): void;

	lerCartao(
		timeout: number,
		onSuccess: (sucess: string) => void,
		onError: (error: string) => void): void;

	lerPin(
		timeout: number,
		onSuccess: (sucess: string) => void,
		onError: (error: string) => void): void;

	finalizar(
		onSuccess: (sucess: string) => void,
		onError: (error: string) => void): void;
}

declare var AWBE: AWBE;