interface Window {
	plugins: Plugins;
}

interface Plugins {
	touchid: TouchID;
}

interface TouchID {
	isAvailable(
		onSuccess: (type: string) => void,
		onError: (error: string) => void): void;

	verifyFingerprint(
		message: string,
		onSuccess: (type: string) => void,
		onError: (error: string) => void): void;

	verifyFingerprintWithCustomPasswordFallback(
		message: string,
		onSuccess: (type: string) => void,
		onError: (error: string) => void): void;

	verifyFingerprintWithCustomPasswordFallbackAndEnterPasswordLabel(
		message: string,
		buttonLabel: string,
		onSuccess: (type: string) => void,
		onError: (error: string) => void): void;

	didFingerprintDatabaseChange(
		callback: (type: string) => void): void;
}

declare var touchid: TouchID;