import { AMBLogger } from './amb.logger';
import { AMBLoglevel } from './amb.log-level';
/**
 * @Serviço @name AMBConsoleLogger
 * Logger para o console do browser.
 *
 * @description
 * O serviço faz parte do módulo @amb/core, este deve ser implementado para a
 * utilização do serviço. Para utilizar o serviço ver a documentação referente ao @injectable
 * do InversifyJS.
 *
 * São disponibilizadas as seguintes severidades para o log:
 *  - debug
 *  - info
 *  - warning
 *  - error
 *
 * @example
 * constructor(public consoleLogger: ConsoleLogger) {}
 *
 * @implements Logger @see {@link Logger}
 *
 * @see {@link http://inversify.io/}
 */
export declare class AMBConsoleLogger implements AMBLogger {
    /** Data atual no momento do log. */
    date: Date;
    log(messagem: string, level: AMBLoglevel): void;
    /**
     * @override
     * Responsável por efetuar o log no nível debug.
     *
     * @param message Mensagem a ser logada.
     */
    private debug(message);
    /**
     * @override
     * Responsável por efetuar o log no nível info.
     *
     * @param message Mensagem a ser logada.
     */
    private info(message);
    /**
     * @override
     * Responsável por efetuar o log no nível warning.
     *
     * @param message Mensagem a ser logada.
     */
    private warning(message);
    /**
     * @override
     * Responsável por efetuar o log no nível error.
     *
     * @param message Mensagem a ser logada.
     */
    private error(message);
    private checkLogLevel(level);
}
